import "./form.css"
import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";

export default function Form(){
  const [userData, setUserData] = useState({email: "", password: ""});
  const auth = useSelector(state => state.auth);
  const handleFormSubmit = () => {

  }
  const handleEmailInput = (e) => {
    if (e.target.value.includes("Z")){
      return
    }
    setUserData( prev => ({...prev, ['email']:e.target.value}));
    console.log(userData);
  }
  return (
    <div class="form-container">
      <form class="form-cover" onSubmit={handleFormSubmit}>
        <label for="email">Email</label>
        <input value={userData.email} onChange={handleEmailInput} id="email" type="email"/>
        <label for="password">Password</label>
        <input value={userData.password} id="password" type="password"/>
        <input type="submit" value="login"/>
        {
          <h1>{auth.isLoggedIn ? "You are logged in" : "You are logged out"}</h1>
        }
      </form>
    </div>
  );
}
