import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import Cookies from 'js-cookie';

const initialState = {
  isLoggedIn: false,
  token: null, // Add token to state
  status: 'idle', // Add status for async operations
  error: null
}

export const login = createAsyncThunk('auth/login', async (userData) => {
  const response = await fetch("http://localhost:3000/login", {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(userData),
  });

  if (!response.ok) {
    throw new Error('Login failed');
  }

  const data = await response.json();
  console.log(data);
  // Optionally store the token in cookies
  Cookies.set('token', data.token);
  return data;
});


const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    logout(state){
      state.isLoggedIn = false;
    },
    extraReducers: (builder) => {
      builder
        .updateLogin(login.pending, (state) => {
          state.status = 'pending';
        })
        .updateLogin(login.fulfilled, (state) => {
          state.isLoggedIn = true;
        })
        .updateLogin(login.rejected, (state, action)=> {
          state.status = "idle";
          state.error = action.error.message;
        })
    }
}})


export const {logout} = authSlice.actions;
export default authSlice.reducer;
