import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  isLoggedIn: false

}

const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    login(state){
      state.isLoggedIn = true;
    },
    logout(state){
      state.isLoggedIn = false;
    }
  }
});


export const {logout, login} = authSlice.actions;
export default authSlice.reducer;
